<script  type="text/javascript" charset="UTF-8" >

    var jsonLocation = <?php echo $jsonLocation; ?>;
    /**
     * Adds markers to the map highlighting the locations of the captials of
     * France, Italy, Germany, Spain and the United Kingdom.
     *
     * @param  {H.Map} map      A HERE Map instance within the application
     */
    function addMarkersToMap(map) {
        var svgMarkup = '<svg data-name="layer1" xmlns="http://www.w3.org/2000/svg" width="28" height="28" xmlns:xlink="http://www.w3.org/1999/xlink" viewBox="0 0 29.52 29.76"><image width="123" height="124" transform="scale(.24)" xlink:href="data:image/png;base64,"/><path d="M15.61 20.2a6.17 6.17 0 1 1 6.18-6.16 6.17 6.17 0 0 1-6.18 6.16zm0-10.48a4.32 4.32 0 1 0 4.32 4.32 4.32 4.32 0 0 0-4.32-4.32z" fill="#ee337c" fill-rule="evenodd"/><circle cx="15.61" cy="14.04" r="3.39" fill="#ef5799"/></svg>';

        var mapIcon = new H.map.Icon( svgMarkup );

        for (var i in jsonLocation){
            Marker = new H.map.Marker(
                {lat:jsonLocation[i]['lat'], lng:jsonLocation[i]['lng'] },
                {icon: mapIcon}
            );
            map.addObject(Marker);
        }

    }
    /**
     * Boilerplate map initialization code starts below:
     */
//Step 1: initialize communication with the platform
    var platform = new H.service.Platform({
        app_id: 'KngCq2F5ZiDAoC5mHcOf',
        app_code: 'B9eBCS_ZNlw3uV-F8JilqQ',
        useHTTPS: true
    });
    var pixelRatio = window.devicePixelRatio || 1;
    var defaultLayers = platform.createDefaultLayers({
        tileSize: pixelRatio === 1 ? 256 : 512,
        ppi: pixelRatio === 1 ? undefined : 320
    });

    var mapTileService = platform.getMapTileService({ 'type': 'base' });

    // Create a tile layer which requests map tiles with an additional 'style'
    // URL parameter set to 'fleet':




    var fleetStyleLayer = mapTileService.createTileLayer(
        'maptile',
        'pedestrian.night',
        256,
        'png8',

        { 'style': 'mini' });

    // Set the new fleet style layer as a base layer on the map:

    //Step 2: initialize a map - this map is centered over Europe
    var map = new H.Map(document.getElementById('map'),
        defaultLayers.normal.map,{
            center: {lat:50, lng:5},
            zoom: 5,
            pixelRatio: pixelRatio,


        });







    var behavior = new H.mapevents.Behavior(new H.mapevents.MapEvents(map));
    // Create the default UI components
    var ui = H.ui.UI.createDefault(map, defaultLayers);
    // Now use the map as required...
    // addMarkersToMap( map.setBaseLayer(fleetStyleLayer));
    var mapEvents =   addMarkersToMap( map.setBaseLayer(fleetStyleLayer));
    // Add event listeners:
    map.addEventListener('tap', function(evt) {
        // Log 'tap' and 'mouse' events:
        console.log(evt.type, evt.currentPointer.type, evt.target);
    });
</script>
